import React from 'react';
import { Dropdown } from 'semantic-ui-react'


export default class Filter extends React.Component {

    languageOptions = [
        {
            key: 'All',
            text: 'All',
            value: 'All',
            image: { avatar: true, src: 'https://i.pinimg.com/originals/0f/8b/28/0f8b2870896edcde8f6149fe2733faaf.jpg' },
        },
        {
          key: 'Ruby',
          text: 'Ruby',
          value: 'Ruby',
          image: { avatar: true, src: 'https://www.ruby-lang.org/images/header-ruby-logo.png' },
        },
        {
          key: 'Rails',
          text: 'Rails',
          value: 'Rails',
          image: { avatar: true, src: 'data:image/png;base64,' },
        },
        {
          key: 'Javascript',
          text: 'Javascript',
          value: 'Javascript',
          image: { avatar: true, src: 'https://i.stack.imgur.com/Mmww2.png' },
        },
        {
          key: 'React',
          text: 'React',
          value: 'React',
          image: { avatar: true, src: 'https://upload.wikimedia.org/wikipedia/commons/thumb/a/a7/React-icon.svg/512px-React-icon.svg.png' },
        },
      ]

      filter = () => (
        <Dropdown
            placeholder='Select Language'
            fluid
            selection
            options={this.languageOptions}
            onChange={(_, selection) => {
                this.props.filterVideos(selection.value)
            }}
        />
      )

    render() {
        return (
            <div>
                {this.filter()}
            </div>
        )
    }
}
